def initialize(options)
  @format     = DEFAULT_FORMAT
  @readme     = Dir['README*'].first
  @source     = false
  @use_cache  = false
  @markup     = nil
  @scripts    = []
  @documents  = []

  options.each do |k,v|
    __send__("#{k}=", v)
  end
end
